mapper.select:
select

from user



    account_name LIKE CONCAT ('%',#{accountName},'%')



    AND status = #{status}



    AND datachange_createtime >= #{createTimeBegin}


    AND datachange_createtime <= #{createTimeEnd}



    AND datachange_updatetime >= #{updateTimeBegin}


    AND datachange_updatetime <= #{updateTimeEnd}



  limit #{offset}, #{pageNo}

select count(*)
from user



    account_name LIKE CONCAT ('%',#{accountName},'%')



    AND status = #{status}



    AND datachange_createtime >= #{createTimeBegin}


    AND datachange_createtime <= #{createTimeEnd}



    AND datachange_updatetime >= #{updateTimeBegin}


    AND datachange_updatetime <= #{updateTimeEnd}
